//Expertenaufgabe
#include &lt;iostream&gt;
#include &lt;iomanip&gt;
#include &lt;stack&gt;

using namespace std;

void showbinary(short deznumber){
	stack&lt;int&gt; binnumbers;
	short r;
	short number = deznumber;
	int i = 1;
	do{
		r = number % 2;
		number = number / 2;
		binnumbers.push(r);
	}while(number != 0);

	while(binnumbers.size() &lt; sizeof(short)*8){
		binnumbers.push(0);
	}

	do{

		cout &lt;&lt; binnumbers.top();
		binnumbers.pop();
		if ((i % 4) == 0){
			cout &lt;&lt; &#039; &#039;;
		}
		i++;
	}while(!binnumbers.empty());
}

int main(){
	short zahl;
	cout &lt;&lt; &quot;Dezimalzahl: &quot;;
	cin &gt;&gt; zahl;
	showbinary(zahl);
}
