				if((letter &gt;= &#039;a&#039; &amp;&amp; letter &lt;= &#039;z&#039;)||(letter &gt;= &#039;A&#039; &amp;&amp; letter &lt;= &#039;Z&#039;)){
					cout &lt;&lt; codieren(letter);
				}else {
					cout &lt;&lt; &quot;Ungueltige Eingabe&quot;;
				}
			}
		}
		cout &lt;&lt; endl;
	} while(weiter);
}

char codieren(char letter){
	char casar;
	if(letter &gt;= &#039;a&#039; &amp;&amp; letter &lt;= &#039;z&#039;){
		if(letter &lt;= &#039;u&#039;){
			casar = letter += 5;
		} else{
			casar = letter -= 21;
		}
	} else if(letter &gt;= &#039;A&#039; &amp;&amp; letter &lt;= &#039;Z&#039;){
		if(letter &lt;= &#039;U&#039;){
			casar = letter += 5;
		} else{
			casar = letter -= 21;
		}
	}else {
		return false;
	}
	return casar;
}
