unsigned int sortiere(float&amp; x, float&amp; y, float&amp; z){
	float hilf;
	int counter = 0;
	do{
		if(x &gt; y){
			hilf = x;
			x = y;
			y = hilf;
			counter++;
		}
		if(y &gt; z){
			hilf = y;
			y = z;
			z = hilf;
			counter++;
		}
	}while(x &gt; y || y &gt; z);

	return counter;
}
