void umdrehen(vector&lt;double&gt;&amp; ve){
	stack&lt;double&gt; s;
	int i = 0;

	while(i &lt; ve.size()){
		s.push(ve.at(i));
		i++;
	}
	ve.clear();
	while(!s.empty()){
		ve.push_back(s.top());
		s.pop();
	}
}
