int nextWiderstand(const vector&lt;int&gt;&amp; v, const int number);
int indenwertebereich(double number, int&amp; exponent);

int main(){
	vector&lt;int&gt; normE6 {100, 150, 220, 330, 470, 680};
	double input;
	int exponent = 0;

	cout &lt;&lt; &quot;Gewuenschter Widerstand: &quot;;
	cin &gt;&gt; input;

	cout &lt;&lt; &quot;Naechster Widerstand: &quot; &lt;&lt; nextWiderstand(normE6, indenwertebereich(input, exponent)) * pow(10, exponent);
}

int indenwertebereich(double number, int&amp; exponent){
	double zahl = number;
	bool notinrange = true;

	do{
		if(zahl &lt;= 1000 &amp;&amp; zahl &gt;= 100){
			notinrange = false;
		}else if(zahl &lt; 100){
			zahl = zahl * 10;
			exponent--;
		}else if(zahl){
			zahl = zahl / 10;
			exponent++;
		}

	}while(notinrange);

	return zahl;
}


int nextWiderstand(const vector&lt;int&gt; &amp; v, int number) {
	int diffrence;
	int lastdiffrence = 681;
	int nextwiderstand = 0;

	for(int element : v){
		diffrence = element - number;

		if(diffrence &lt; 0){
			diffrence = diffrence + (diffrence * (-2));
		}

		if(diffrence &lt; lastdiffrence){
			nextwiderstand = element;
		}

		lastdiffrence = diffrence;
	}

	return nextwiderstand;
}
